    #region 上传文件

    /// <summary>
    /// 上传文件
    /// </summary>
    /// <param name="formFile"></param>
    /// <returns></returns>
    [HttpPost("UploadFile")]
    public async Task<ApiResult> UploadFile(IFormFile formFile)
    {
        if (formFile.Length <= 0) return ApiResult.InternalServerError();

        var fileName = formFile.FileName;
        var path = Path.Combine(App.RootUploadPath, fileName);

        FileStream fs = new(path, FileMode.OpenOrCreate, FileAccess.ReadWrite);
        await formFile.CopyToAsync(fs);
        fs.Close();

        FileStream stream = new(path, FileMode.Open);

        return await _weComPushService.WeComToUploadFile(stream, WeComUploadType.File);
    }

    #endregion 上传文件
